mov     rcx, QWORD PTR [rbp+24]    ; Load second parameter

; Add numbers
add     rax, rcx                   ; Add the numbers

; Function epilogue
pop     rbp
ret

; Calling code
mov     edi, 1                     ; First argument
mov     esi, 2                     ; Second argument
call    add                        ; Call the function
